pragma solidity ^0.4.4;

import './Campaign.sol';
import 'openzeppelin-solidity/contracts/math/SafeMath.sol';

contract CampaignHub {
    using SafeMath for uint256;

    address public owner;
    uint public numOfCampaigns;

    event NewCampaign(uint id, string title, uint deadline, uint8 matchRate, address addr, address creator);
    event ContributionSent(address campaignAddress, address contributor, uint amount);

    Campaign[] public campaigns;

    mapping(uint => address) public campaignToOwner;
    mapping(address => uint) ownerCampaignCount;

    constructor () internal {
        owner = msg.sender;
        numOfCampaigns = 0;
    }

    /**
    * Create a new Campaign contract
    * [0] -> new Campaign contract address
    */
    function createCampaign(string _title, uint _deadline, uint8 _matchRate) external {
        require(block.timestamp >= _deadline);

        Campaign cp = new Campaign(_title, _deadline, _matchRate, msg.sender);
        uint id = campaigns.push(cp) - 1;
        campaignToOwner[id] = msg.sender;
        ownerCampaignCount[msg.sender]++;
        numOfCampaigns++;
        emit NewCampaign(numOfCampaigns, _title, _deadline, _matchRate, cp, msg.sender);
    }

    /**
    * Allow senders to contribute to a Campaign by it's address. Calls the fund() function in the Campaign
    * contract and passes on all value attached to this function call
    * [0] -> contribution was sent
    */
    function contribute(address _campaignAddress, bool _isMatchVault) external payable {
        _preValidateContribution(msg.sender, msg.value);

        Campaign deployedCampaign = Campaign(_campaignAddress);

        require(deployedCampaign.campaignHub() != address(0));

        if (_isMatchVault) {
            deployedCampaign.fundVault.value(msg.value)(msg.sender);
        } else {
            deployedCampaign.fundDirect.value(msg.value)(msg.sender);
        }
    }

    /**
     * @dev Validation of an incoming contribution.
     * @param _contributor Address performing the campaign contribution
     * @param _weiAmount Value in wei involved in the purchase
     */
    function _preValidateContribution(address _contributor, uint _weiAmount) internal {
        require(_contributor != address(0));
        require(_weiAmount != 0);
    }
}

